function Recipe(e){this.id=e.id,this.title=e.title,this["yield"]=e["yield"],this.content=e.content,this.ingredients=e.ingredients}var ajaxResponse=function(e){for(var t="",i=0;i<e.length;i++){var n=new Recipe(e[i]);currentUser.fav_ids().includes(n.id)?n.fav="unfavorite":n.fav="favorite",t+=n.renderLi()}$(".list-group").html(t)};Recipe.prototype.renderLi=function(){return Recipe.template(this)},$(document).ready(function(){Recipe.templateSource=$("#index-template").html(),Recipe.template=Handlebars.compile(Recipe.templateSource),$.get("/recipes.json").done(ajaxResponse),$("select").change(function(){var e=$(this).val();$.ajax({url:"/recipes.json",method:"GET",contentType:"application/json; charset=utf-8",data:{ingredient:e}}).done(ajaxResponse)})});